source.buildSpec:
{
  "version": "0.2",
  "phases": {
    "install": {
      "runtime-versions": {
        "nodejs": "18"
      },
      "commands": [
        "cd cdk-app",
        "npm install"
      ]
    },
    "build": {
      "commands": [
        "npm run build",
        "npm run test"
      ]
    }
  },
  "artifacts": {
    "base-directory": "cdk-app",
    "files": [
      "**/*"
    ]
  }
}

{
  "version": "0.2",
  "phases": {
    "install": {
      "runtime-versions": {
        "nodejs": "18"
      },
      "commands": [
        "npm install -g aws-cdk",
        "cd cdk-app",
        "npm install"
      ]
    },
    "build": {
      "commands": [
        "npm run build",
        "cdk deploy --require-approval never"
      ]
    }
  }
}
